import { useEffect, useState } from "react";
import { Badge, Button, Card } from "react-bootstrap";
import ConfirmationModal from "../../../../components/Modal/ConfirmationModal";
import ModalComponent from "../../../../components/Modal/Modal";
import "./Groups.scss";

const API_URL = "https://run.mocky.io/v3/6a493a14-4155-463c-9f08-24be7e91cb6d";

const Groups = () => {
  const [groups, setGroups] = useState([]);
  const [ show, setShow ] = useState(false);
  const [ showConfirmation, setShowConfirmation ] = useState(false);
  const [ idGroup, setIdGroup ] = useState('');

  const loadData = async () => {
    const response = await fetch(API_URL);
    const data = await response.json();
    setGroups(data);
  };

  useEffect(() => {
    loadData();
  }, []);

  const handleModal = (id) => {
    setIdGroup(id);
    setShow(true);
  };

  const handleModalConfirmation = (id) => {
    setIdGroup(id);
    setShowConfirmation(true);
  };

  const handleAbandon = () => {
    setShowConfirmation(false);
  };

  return (
    <section className="groups">
      <h3>Groups</h3>
      <div className="groups__cards">
        {groups.map(({ image, name, description, language, id, join }) => (
          <div key={id}>
            <Card >
              <Card.Img className="card__img" src={image} />
              <Card.Body>
                <p>
                  <Badge variant="secondary">{language}</Badge>
                </p>
                <h4>{name}</h4>
                <p>{description}</p>
                {join && <Badge variant="primary">joined</Badge>}
                <div>
                  {!join && <Button variant="secondary" onClick={() => handleModal(id)}>Join</Button>}
                  {join && <Button variant="secondary" onClick={() => handleModalConfirmation(id)}>Abandon</Button>}
                </div>
              </Card.Body>
            </Card>
            <ConfirmationModal show={showConfirmation} onClose={()=> setShowConfirmation(false)} handleConfirmation={handleAbandon} />
          </div>
        ))}
      </div>
      <ModalComponent show={show} onClose={() => setShow(false)} />
    </section>
  );
};

export default Groups;
